function Main() {

    function handleEditProfileClick(e) {
        if (e.target.className === 'profile__edit-avatar') {
            console.log('Надави на меня');
        }
        //e.target.classList.add('popup_opened');
    }


    return(
        <main className="page">
        <section className="profile">
            <div className="profile__content">
            <button 
                    id="edit-avatar-button"
                    type="button"                     
                    className="profile__edit-avatar"
                    onClick={handleEditProfileClick}
            >
                <img src="https://mykaleidoscope.ru/x/uploads/posts/2022-10/1666206241_12-mykaleidoscope-ru-p-kartinka-na-zastavku-oboi-12.jpg" alt="аватар профиля" className="profile__avatar" />
                <div className="profile__edit-avatar-icon" />
            </button>
            <div className="profile__info">
                <div className="profile__container">
                <h1 id="profile__name" className="profile__name">жак ив</h1>
                <button
                    type="button"
                    className="button profile__edit-button"
                ></button>
                </div>
                <p id="profile__job" className="profile__job" />
            </div>
            </div>
            <button type="button" className="button profile__add-button"></button>
        </section>
        </main>
    );
}

export default Main;


