import { useState, useEffect } from 'react';
import {api} from '../utils/api.js';


function Main(props) {
    const [userName, setUserName] = useState('');
    const [userDescription, setUserDescription] = useState('');
    const [userAvatar, setUserAvatar] = useState('');

    useEffect(() => {
        api.getUserInfo()
            .then(res => {
                setUserName(res.name);
                setUserDescription(res.about);
                setUserAvatar(res.avatar);
            })
            .catch(err => console.log(err));
    }, []);

    return(
    <main className="page">
        <section className="profile">
            <div className="profile__content">
                <button 
                    className="profile__edit-avatar"
                    id="edit-avatar-button"
                    type="button"                     
                    onClick={props.onEditAvatar}
                >
                    <img src="https://mykaleidoscope.ru/x/uploads/posts/2022-10/1666206241_12-mykaleidoscope-ru-p-kartinka-na-zastavku-oboi-12.jpg" alt="аватар профиля" 
                        className="profile__avatar" style={{ backgroundImage: `url(${userAvatar})` }}/>
                    <div className="profile__edit-avatar-icon" />
                </button> 
                <div className="profile__info">
                    <div className="profile__container">
                        <h1 id="profile__name" className="profile__name">{userName}</h1>
                        <button
                            type="button"
                            className="button profile__edit-button"
                            onClick={props.onEditProfile}
                        >
                        </button>
                    </div>
                    <p id="profile__job" className="profile__job">{userDescription}</p>
                </div>
            </div>
            <button 
                type="button" 
                className="button profile__add-button"
                onClick={props.onAddPlace}
                >
            </button>
        </section>


        <section className="elements"></section>


        </main>
    );
}

export default Main;



