function Main() {

    function handleEditProfileClick() {
            //console.log('Надави на меня');
            const profileEdit = document.querySelector('#popup-edit');
            profileEdit.classList.add('popup_opened');
    }

    function handleAddPlaceClick() {
                //console.log('Надави на меня');
                const profileAdd = document.querySelector('#popup-add');
                profileAdd.classList.add('popup_opened');
    }

    function handleEditAvatarClick(){
                //console.log('Надави на меня');
                const profileAdd = document.querySelector('#popup-add');
                profileAdd.classList.add('popup_opened');
    }

    return(
        <main className="page">
        <section className="profile">
            <div className="profile__content">
            <button 
                    id="edit-avatar-button"
                    type="button"                     
                    className="profile__edit-avatar"
                    onClick={handleEditAvatarClick}
            >
                <img src="https://mykaleidoscope.ru/x/uploads/posts/2022-10/1666206241_12-mykaleidoscope-ru-p-kartinka-na-zastavku-oboi-12.jpg" alt="аватар профиля" className="profile__avatar" />
                <div className="profile__edit-avatar-icon" />
            </button> 
            <div className="profile__info">
                <div className="profile__container">
                <h1 id="profile__name" className="profile__name">жак ив</h1>
                <button
                    type="button"
                    className="button profile__edit-button"
                    onClick={handleEditProfileClick}
                ></button>
                </div>
                <p id="profile__job" className="profile__job" />
            </div>
            </div>
            <button 
                type="button" 
                className="button profile__add-button"
                onClick={handleAddPlaceClick}></button>
        </section>


        <section className="elements"></section>
        {/*Popup: Изменить профиль*/}
        <div className="popup popup-edit" id="popup-edit">
            <div className="popup__container">
            <h2 className="popup__heading">Редактировать профиль</h2>
            <form
                className="popup-edit__form popup__form"
                name="profileEditPopupForm"
                noValidate=""
            >
                <input
                id="name-input"
                type="text"
                name="name"
                className="popup__input popup__input_type_name"
                placeholder="Введите имя"
                minLength={2}
                maxLength={40}
                required=""
                />
                <span className="popup__input-error name-input-error" />
                <input
                id="job-input"
                type="text"
                name="job"
                className="popup__input popup__input_type_job"
                placeholder="Введите профессию"
                minLength={2}
                maxLength={200}
                required=""
                />
                <span className="popup__input-error job-input-error" />
                <button className="popup__button" type="submit">
                Сохранить
                </button>
            </form>
            <button
                type="button"
                className="button popup__button-close popup-edit__button-close"
            />
            </div>
        </div>
        {/*Popup: Добавить место*/}
        <div className="popup popup-add" id="popup-add">
            <div className="popup__container">
            <h2 className="popup__heading">Новое место</h2>
             <form
                className="popup-add__form popup__form"
                name="profileAddPopupForm"
                noValidate=""
            >
                <input
                id="place-input"
                type="text"
                name="place"
                className="popup__input popup__input_type_place"
                placeholder="Название"
                minLength={2}
                maxLength={30}
                required=""
                />
                <span className="popup__input-error place-input-error" />
                <input
                id="image-input"
                name="image"
                className="popup__input popup__input_type_image"
                placeholder="Ссылка на картинку"
                type="url"
                required=""
                />
                <span className="popup__input-error image-input-error" />
                <button className="popup__button" type="submit">
                Создать
                </button>
            </form>
            <button
                type="button"
                className="button popup__button-close popup-add__button-close"
            />
            </div>
        </div>
        {/*Popup: обновить аватар*/}
        <div className="popup popup-edit-avatar" id="popup-edit-avatar">
             <div className="popup__container">
             <h2 className="popup__heading">Обновить аватар</h2>
             <form
                 className="popup-edit-avatar__form popup__form"
                name="profileAddPopupForm"
                noValidate=""
            >
                <input
                id="avatar-input"
                name="link"
                className="popup__input popup__input_type_link"
                placeholder="Ссылка на картинку"
                type="url"
                required=""
                />
                <span className="popup__input-error avatar-input-error" />
                <button className="popup__button" type="submit">
                Сохранить
                </button>
            </form>
            <button
                type="button"
                className="button popup__button-close popup-edit-avatar__button-close"
            />
            </div>
        </div>

        </main>
    );
}

export default Main;


