import React, { useState } from 'react'
import Header from './components/Header.js';
import Main from './components/Main.js';
import Footer from './components/Footer.js';
import PopupWithForm from './components/PopupWithForm.js';
//import ImagePopup from './ImagePopup.js';

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }



  return (
    <div className="page">
        <Header/>
        <Main
          onEditProfile={handleEditProfileClick}         
          onAddPlace={handleAddPlaceClick}
          onEditAvatar={handleEditAvatarClick}
        />

        <Footer/>
        <PopupWithForm
          title="Редактировать профиль"
          name="edit"
          submitButton="Сохранить"
          isOpen={isEditProfilePopupOpen}
        >
          <input type="text" className="form__input form__input_type_name" name="name" placeholder="Имя" required minLength={2} maxLength={40} />
          <span className="form__input-error name-error" />
          <input type="text" className="form__input form__input_type_job" name="about" placeholder="О себе" required minLength={2} maxLength={200} />
          <span className="form__input-error job-error" />
        </PopupWithForm>
        <PopupWithForm
        title="Новое место"
        name="add"
        submitButton="Создать"
        isOpen={isAddPlacePopupOpen}
      >
        <input
          type="text"
          className="form__input form__input_type_place-name"
          name="name"
          placeholder="Название"
          required
          minLength={1}
          maxLength={30}
        />
        <span className="form__input-error place-name-error" />
        <input
          type="url"
          className="form__input form__input_type_link"
          name="link"
          placeholder="Ссылка на картинку"
          required
        />
        <span className="form__input-error link-error" />
      </PopupWithForm>
      <PopupWithForm
        title="Обновить аватар"
        name="edit-avatar"
        submitButton="Сохранить"
        isOpen={isEditAvatarPopupOpen}
      >
        <input
          type="url"
          className="form__input form__input_type_link"
          name="avatar"
          placeholder="Ссылка на аватар"
          required
        />
        <span className="form__input-error avatar-error" />
      </PopupWithForm>
    </div>
  );
}


export default App;
