//export PopupWithForm {

function Main() {

    function handleEditProfileClick() {
            //console.log('Надави на меня');
            const profileEdit = document.querySelector('#popup-edit');
            profileEdit.classList.add('popup_opened');
    }

    function handleAddPlaceClick() {
                //console.log('Надави на меня');
                const profileAdd = document.querySelector('#popup-add');
                profileAdd.classList.add('popup_opened');
    }

    function handleEditAvatarClick(){
                //console.log('Надави на меня');
                const profileAdd = document.querySelector('#popup-edit-avatar');
                profileAdd.classList.add('popup_opened');
    }

    return(
        <main className="page">
        <section className="profile">
            <div className="profile__content">
            <button 
                    id="edit-avatar-button"
                    type="button"                     
                    className="profile__edit-avatar"
                    onClick={handleEditAvatarClick}
            >
                <img src="https://mykaleidoscope.ru/x/uploads/posts/2022-10/1666206241_12-mykaleidoscope-ru-p-kartinka-na-zastavku-oboi-12.jpg" alt="аватар профиля" className="profile__avatar" />
                <div className="profile__edit-avatar-icon" />
            </button> 
            <div className="profile__info">
                <div className="profile__container">
                <h1 id="profile__name" className="profile__name">жак ив</h1>
                <button
                    type="button"
                    className="button profile__edit-button"
                    onClick={handleEditProfileClick}
                ></button>
                </div>
                <p id="profile__job" className="profile__job" />
            </div>
            </div>
            <button 
                type="button" 
                className="button profile__add-button"
                onClick={handleAddPlaceClick}></button>
        </section>


        <section className="elements"></section>


        </main>
    );
}

export default Main;


